from .validator import validate
import pytest

@pytest.mark.parametrize("test_url1, test_url2, test_url3", [('', 'notcorrecturl', 'https://www..com/')])
def test_validator(test_url1, test_url2, test_url3):
    assert validate(test_url1)['existence'] == 'URL обязателен'
    assert validate(test_url2)['validate'] == 'Некорректный URL'
    assert validate(test_url3)['size'] == 'URL превышает 255 символов'

